package com.umpay.proxyservice;import java.net.URISyntaxException;import com.bs3.ioc.core.BeansContext;import com.bs3.utils.MyLog;import com.umpay.proxyservice.util.SpringUtil;/** * 启动服务 * @author xuxiaojia */public class StartService {	private static final MyLog log = MyLog.getLog(StartService.class);		public static void main(String[] args) throws URISyntaxException, Exception {		BeansContext ctx=BeansContext.getInstance();		SpringUtil.getInstance();		ctx.setMappingFile("resource/mina2niocs.properties");		try {			ctx.start();			log.info("初始化超频超额前置服务成功");		} catch (Exception e) {			log.error(e,"初始化超频超额前置服务失败");		}		}}